import { useState } from 'react';
import '../styles/Add.css';  // ya apni Find.css agar hai to wo use kar sakte ho
import axios from "axios";

export function Find() {
  const [id, setId] = useState('');
  const [employee, setEmployee] = useState(null);

  async function findHandler(e) {
    e.preventDefault();
    try {
      const response = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/employees/${id}`
      );
      setEmployee(response.data);
    } catch (err) {
      setEmployee(null);
      alert("Employee Not Found: " + (err.response?.data?.message || err.message));
    }
  }

  return (
    <div className="form-container">
      <h2>Find Employee by ID</h2>
      <form onSubmit={findHandler}>
        <input
          type="text"
          placeholder="Enter Employee ID"
          onChange={(e) => setId(e.target.value)}
          value={id}
          required
        /><br/><br/><br/>
        <button type="submit">
          Find
        </button>
      </form>

      {employee && (
        <div style={{marginTop: '20px'}}>
          <h3>Employee Details</h3>
          <p><strong>Emp No:</strong> {employee.empNo}</p>
          <p><strong>Emp Name:</strong> {employee.empName}</p>
          <p><strong>Emp Salary:</strong> {employee.empSal}</p>
        </div>
      )}
    </div>
  );
}
